water_lines_casing
(select way,waterway,disused,name, case when tunnel in ('yes','true','1') then 'yes'::text else tunnel end as tunnel from planet_osm_line where waterway in ('weir','river','canal','derelict_canal','stream','drain') and (bridge is null or bridge not in ('yes','true','1','aqueduct')) order by z_order ) as water_lines_casing &datasource-settings;
water_areas
(select way,"natural",waterway,landuse,name,way_area from planet_osm_polygon where waterway in ('dock','mill_pond','riverbank','canal') or landuse in ('reservoir','water','basin') or "natural" in ('lake','water','land','marsh','wetland','glacier') order by z_order,way_area desc ) as water_areas &datasource-settings;
