leisure
(select way,aeroway,amenity,landuse,leisure,man_made,military,"natural",power,tourism,name from planet_osm_polygon where landuse is not null or leisure is not null or aeroway in ('apron','aerodrome') or amenity in ('parking','university','college','school','hospital','kindergarten','grave_yard') or military in ('barracks','danger_area') or "natural" in ('field','beach','heath','mud','wood') or power in ('station','sub_station') or tourism in ('attraction','camp_site','caravan_site','picnic_site','zoo') order by z_order,way_area desc ) as leisure
&datasource-settings;
sports_grounds
(select way,leisure from planet_osm_polygon where leisure in ('sports_centre','stadium') order by z_order,way_area desc) as sports_grounds &datasource-settings;
