minor-roads-fill-links minor-roads-fill
(select way,highway,horse,bicycle,foot,construction,aeroway,service, case when tunnel in ('yes','true','1') then 'yes'::text else tunnel end as tunnel, case when bridge in ('yes','true','1') then 'yes'::text else bridge end as bridge, case when railway in ('spur','siding') or (railway='rail' and service in ('spur','siding','yard')) then 'spur-siding-yard'::text else railway end as railway from planet_osm_line where highway is not null or aeroway in ('runway','taxiway') or railway in ('light_rail','narrow_gauge','funicular','rail','subway','tram','spur','siding','platform','disused','abandoned','construction') order by z_order) as roads
&datasource-settings;
