yellow-red-blue-track
((SELECT osm_id,way,route,name,ref,kct_yellow,kct_red,kct_blue,marked_trail_yellow,marked_trail_red,marked_trail_blue,char_length(ref) AS length FROM &prefix;_line WHERE osm_id > 0 AND ((kct_yellow IS NOT NULL) OR (marked_trail_yellow IS NOT NULL)) AND ((kct_red IS NOT NULL) OR (marked_trail_red IS NOT NULL)) AND ((kct_green IS NULL) AND (marked_trail_green IS NULL)) AND ((kct_blue IS NOT NULL) OR (marked_trail_blue IS NOT NULL))) UNION (SELECT osm_id,way,route,name,ref,kct_yellow,kct_red,kct_blue,marked_trail_yellow,marked_trail_red,marked_trail_blue,char_length(ref) AS length FROM &prefix;_track_rels WHERE ((kct_yellow IS NOT NULL) OR (marked_trail_yellow IS NOT NULL)) AND ((kct_red IS NOT NULL) OR (marked_trail_red IS NOT NULL)) AND ((kct_green IS NULL) AND (marked_trail_green IS NULL)) AND ((kct_blue IS NOT NULL) OR (marked_trail_blue IS NOT NULL)))) as yellow_red_blue_track &datasource-settings;
yellow-red-green-track
((SELECT osm_id,way,route,name,ref,kct_yellow,kct_red,kct_green,marked_trail_yellow,marked_trail_red,marked_trail_green,char_length(ref) AS length FROM &prefix;_line WHERE osm_id > 0 AND ((kct_yellow IS NOT NULL) OR (marked_trail_yellow IS NOT NULL)) AND ((kct_red IS NOT NULL) OR (marked_trail_red IS NOT NULL)) AND ((kct_green IS NOT NULL) OR (marked_trail_green IS NOT NULL)) AND ((kct_blue IS NULL) AND (marked_trail_blue IS NULL))) UNION (SELECT osm_id,way,route,name,ref,kct_yellow,kct_red,kct_green,marked_trail_yellow,marked_trail_red,marked_trail_green,char_length(ref) AS length FROM &prefix;_track_rels WHERE ((kct_yellow IS NOT NULL) OR (marked_trail_yellow IS NOT NULL)) AND ((kct_red IS NOT NULL) OR (marked_trail_red IS NOT NULL)) AND ((kct_green IS NOT NULL) OR (marked_trail_green IS NOT NULL)) AND ((kct_blue IS NULL) AND (marked_trail_blue IS NULL)))) as yellow_red_green_track &datasource-settings;
yellow-red-green-blue-track
((SELECT osm_id,way,route,name,ref,kct_yellow,kct_red,kct_green,kct_blue,marked_trail_yellow,marked_trail_red,marked_trail_green,marked_trail_blue,char_length(ref) AS length FROM &prefix;_line WHERE osm_id > 0 AND ((kct_yellow IS NOT NULL) OR (marked_trail_yellow IS NOT NULL)) AND ((kct_red IS NOT NULL) OR (marked_trail_red IS NOT NULL)) AND ((kct_green IS NOT NULL) OR (marked_trail_green IS NOT NULL)) AND ((kct_blue IS NOT NULL) OR (marked_trail_blue IS NOT NULL))) UNION (SELECT osm_id,way,route,name,ref,kct_yellow,kct_red,kct_green,kct_blue,marked_trail_yellow,marked_trail_red,marked_trail_green,marked_trail_blue,char_length(ref) AS length FROM &prefix;_track_rels WHERE ((kct_yellow IS NOT NULL) OR (marked_trail_yellow IS NOT NULL)) AND ((kct_red IS NOT NULL) OR (marked_trail_red IS NOT NULL)) AND ((kct_green IS NOT NULL) OR (marked_trail_green IS NOT NULL)) AND ((kct_blue IS NOT NULL) OR (marked_trail_blue IS NOT NULL)))) as yellow_red_green_blue_track &datasource-settings;
international-track
((SELECT osm_id,way,route,name,ref,network,char_length(ref) AS length FROM &prefix;_line WHERE osm_id > 0 AND route='foot' AND (network='e-road' OR network='nwn' OR network='iwn')) UNION (SELECT osm_id,way,route,name,ref,network,char_length(ref) AS length from &prefix;_track_rels WHERE route='foot' AND (network='e-road' OR network='nwn' OR network='iwn'))) as international_tacks
&datasource-settings;
mtb_incline
(select osm_id,way,incline,"mtb:scale:uphill" from planet_osm_line where ("mtb:scale:uphill" is not null and incline in ('up','down'))) as mtb_basic
&datasource-settings;
mtb_shield5
(select osm_id,way from planet_osm_line where ("mtb:scale" is not null or mtb is not null or "mtb:scale:uphill" is not null or route='mtb') and "mtb:scale" in ('5','5+','5-')) as mtb_basic
&datasource-settings;
mtb_shield4
(select osm_id,way from planet_osm_line where ("mtb:scale" is not null or mtb is not null or "mtb:scale:uphill" is not null or route='mtb') and "mtb:scale" in ('4','4+','4-')) as mtb_basic
&datasource-settings;
mtb_shield3
(select osm_id,way from planet_osm_line where ("mtb:scale" is not null or mtb is not null or "mtb:scale:uphill" is not null or route='mtb') and "mtb:scale" in ('3','3+','3-')) as mtb_basic
&datasource-settings;
mtb_shield2
(select osm_id,way from planet_osm_line where ("mtb:scale" is not null or mtb is not null or "mtb:scale:uphill" is not null or route='mtb') and "mtb:scale" in ('2','2+','2-')) as mtb_basic
&datasource-settings;
mtb_shield1
(select osm_id,way from planet_osm_line where ("mtb:scale" is not null or mtb is not null or "mtb:scale:uphill" is not null or route='mtb') and "mtb:scale" in ('1','1+','1-')) as mtb_basic
&datasource-settings;
mtb_shield0
(select osm_id,way from planet_osm_line where ("mtb:scale" is not null or mtb is not null or "mtb:scale:uphill" is not null or route='mtb') and "mtb:scale" in ('0','0+')) as mtb_basic
&datasource-settings;
access
(select way,access,highway from planet_osm_line where access is not null) as access
&datasource-settings;
trams
(select way from planet_osm_line where railway='tram' and (tunnel is null or tunnel not in ('yes','true','1'))) as trams
&datasource-settings;
guideways
(select way from planet_osm_line where highway='bus_guideway' and (tunnel is null or tunnel not in ('yes','true','1'))) as guideways
&datasource-settings;
admin-01234
(select way,admin_level from planet_osm_roads where "boundary"='administrative' and admin_level in ('2', '3') ) as admin &datasource-settings;
country state capital
(select way,place,upper(name),name,ref from planet_osm_point where place in ('country','state') or (place in ('city','metropolis') and capital='yes') ) as placenames &datasource-settings;
city town
(select way,place,upper(name),name from planet_osm_point where place in ('city','metropolis','town','large_town','small_town') and (capital is null or capital<>'yes') ) as placenames &datasource-settings;
suburb village hamlet
(select way,place,name from planet_osm_point where place in ('suburb','village','large_village','hamlet','locality') ) as placenames &datasource-settings;
stations
(select way,name,railway,aerialway,disused from planet_osm_point where railway in ('station','halt','tram_stop') or aerialway='station' ) as stations
&datasource-settings;
stations
(select way,name,railway,aerialway,disused from planet_osm_polygon where railway in ('station','halt','tram_stop') or aerialway='station' ) as stations
&datasource-settings;
stations symbols points
(select * from &prefix;_point where barrier IS NOT NULL or amenity IS NOT NULL or tourism is NOT NULL or information is NOT NULL or railway is NOT NULL or "natural" is NOT NULL or highway is NOT NULL or aeroway is NOT NULL or aerialway is NOT NULL or power_source is NOT NULL or man_made is NOT NULL or shop is NOT NULL) as amenity &datasource-settings;
stations symbols-poly points
(select * from &prefix;_polygon where barrier IS NOT NULL or amenity IS NOT NULL or tourism is NOT NULL or railway is NOT NULL or "natural" is NOT NULL or highway is NOT NULL or aeroway is NOT NULL or aerialway is NOT NULL or power_source is NOT NULL or man_made is NOT NULL or shop is NOT NULL) as amenity &datasource-settings;
power
(select way from planet_osm_line where "power"='line') as power &datasource-settings;
power_towers
(select way from planet_osm_point where power='tower') as power_towers &datasource-settings;
directions
(select way, case when oneway in ('yes','true','1') then 'yes'::text else oneway end as oneway from planet_osm_line where oneway is not null and (highway is not null or railway is not null or waterway is not null) ) as directions
&datasource-settings;
roads-text
(select way,highway,aeroway,name,ref,admin_level,char_length(ref) as length, case when bridge in ('yes','true','1') then 'yes'::text else bridge end as bridge from planet_osm_line where waterway IS NULL and leisure IS NULL and landuse IS NULL and admin_level IS NULL and (name is not null or ref is not null) ) as roads
&datasource-settings;
text
(select way,amenity,highway,leisure,landuse,man_made,"natural",place,tourism,information,ele,name,ref,shop,military,aeroway,'yes'::text as point from planet_osm_point where amenity is not null or leisure is not null or landuse is not null or tourism is not null or information is not null or "natural" is not null or man_made in ('lighthouse','windmill') or place='island' or highway='motorway_junction' or military='danger_area' or aeroway='gate' or shop='bicycle' ) as text
&datasource-settings;
text-poly
(select way,aeroway,amenity,highway,leisure,landuse,man_made,"natural",place,tourism,NULL as ele,name,ref,shop,military,'no'::text as point from planet_osm_polygon where amenity is not null or leisure is not null or landuse is not null or tourism is not null or "natural" is not null or man_made in ('lighthouse','windmill') or place='island' or military='danger_area' or shop='bicycle' ) as text
&datasource-settings;
area-text
(select way,way_area,name from planet_osm_polygon where name is not null and (waterway is null or waterway <> 'riverbank') and admin_level is null order by z_order,way_area desc ) as text
&datasource-settings;
cycleway-text
(select way, ref, name, lcn, ncn, rcn from planet_osm_line where ref is not null and (lcn is not null or ncn is not null or rcn is not null)) as cycleway
&datasource-settings;
boundary
(select way,name from planet_osm_polygon where boundary='national_park') as boundary
&datasource-settings;
